<style scoped>
.note-content-board {
  display: flex;
  justify-content: space-between;
  width: 1200px;
  margin: 0px auto 200px;
}

.show-catalogue /deep/ .note-content-catalogue-board {
  width: 20%;
}

.show-catalogue .note-details {
  width: calc(80% - 16px);
}

.note-details {
